import Card from '../UI/Card';
import classes from './Cart.module.css';
import CartItem from './CartItem';
import { useSelector } from 'react-redux';



const Cart = (props) => {
  const cartItems = useSelector(state => state.cart.items)
  const count = useSelector(state => state.cart.count)

  return (
    <Card className={classes.cart}>
      <h2>Your Shopping Cart</h2>
      <ul>
        {cartItems.map(item => (
          <CartItem
          item={{ id: 1, title: 'Frog', quantity: count, total: count*6, price: 6 }}
        />
        ))}

        <CartItem
          item={{ id:2, title: 'Hamster', quantity: count, total: count*10, price: 10 }}
        />
        <CartItem
          item={{ id:3, title: 'Lizard', quantity: count, total: count*25, price: 25 }}
        />
      </ul>
    </Card>
  );
};

export default Cart;
